export default async function RootLayout({ children }) {
  // ðŸ§  Ð’Ð—Ð˜ÐœÐÐœÐ• Ð¿Ð¾Ð´ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸Ñ‚Ðµ Ð½Ð° ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ñ Ñ ID 17 (Artdeco ASIAN SPA)
  // const res = await fetch(
  //   "https://nardis.rosset.website/wp-json/wc/v3/products/categories?parent=17",
  //   {
  //     headers: {
  //       Authorization:
  //         "Basic " +
  //         Buffer.from(
  //           `${process.env.WC_CONSUMER_KEY}:${process.env.WC_CONSUMER_SECRET}`
  //         ).toString("base64"),
  //     },
  //     next: { revalidate: 60 }, // SSR + ÐºÐµÑˆÐ¸Ñ€Ð°Ð½Ðµ Ð·Ð° 60 ÑÐµÐºÑƒÐ½Ð´Ð¸
  //   }
  // );
  // NEW with all cats and subcats
  // Ð’ layout.js, Ð·Ð°Ð¼ÐµÐ½Ð¸ fetch-Ð° ÑÑŠÑ ÑÐ»ÐµÐ´Ð½Ð¾Ñ‚Ð¾:
  const res = await fetch(
    "https://nardis.rosset.website/wp-json/wc/v3/products/categories?per_page=100",
    {
      headers: {
        Authorization:
          "Basic " +
          Buffer.from(
            `${process.env.WC_CONSUMER_KEY}:${process.env.WC_CONSUMER_SECRET}`
          ).toString("base64"),
      },
      next: { revalidate: 60 },
    }
  );

  const allCategories = await res.json();

  // Ð“Ñ€ÑƒÐ¿Ð¸Ñ€Ð°Ð½Ðµ Ð½Ð° ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸ Ð¿Ð¾ parent-child
  const parentCategories = allCategories.filter((cat) => cat.parent === 0);
  const categoriesWithChildren = parentCategories.map((parent) => {
    const children = allCategories.filter((cat) => cat.parent === parent.id);
    return {
      ...parent,
      subcategories: children,
    };
  });

  // const subcategories = await res.json();

  return (
    <html lang="en">
      <body className={roboto.className}>
        <StoreNavigation categories={categoriesWithChildren} />

        {children}
        <FooterSection />
      </body>
    </html>
  );
}
